function init() {
    var canvas = document.getElementById('canvas');
    var ctx = canvas.getContext('2d');
    
    var tablero= new Image();
    tablero.src='https://image.freepik.com/foto-gratis/plank--de-madera--textura--madera_318727.jpg'
    
    var carta = new Image();
    carta.src = 'data:image/jpeg;base64,';
    
    var carta2 = new Image();
    carta2.src ='data:image/jpeg;base64,'
    
    
    
    var card = {
        x: 100,
        y: 100,
        widht:50,
        height:50,
        selec:false,
        draw: function() {
            ctx.drawImage(carta,this.x,this.y,this.widht,this.height);
        }
    };
    
    var card2 = {
        x: 100,
        y: 200,
        widht:50,
        height:50,
        selec:false,
        draw: function() {
            ctx.drawImage(carta2,this.x,this.y,this.widht,this.height);
        }
    };
    
    var cartas=[card,card2]
    
    function clear() {
        ctx.fillStyle = 'rgba(255,255,255,0.3)';
        ctx.fillRect(0,0,canvas.width,canvas.height);
    }
    
    function draw() {
        
        clear();
        ctx.drawImage(tablero,0,0,1000,800);
        for (var i = 0; i < cartas.length; i++) {
            cartas[i].draw();
        }

        window.requestAnimationFrame(draw);
    }
    
    canvas.addEventListener('mousedown', function(e){
        
        var p = $( "canvas" );
        var position = p.position();
        console.log("lao")
        console.log(position.left)
        console.log(position.top)
        
        for (var i = 0; i < cartas.length; i++) {
            if ((cartas[i].x<(e.clientX -100) && (cartas[i].x+cartas[i].widht)>(e.clientX-100))&&
                (cartas[i].y<(e.clientY -100) && (cartas[i].y+cartas[i].height)>(e.clientY-100))) {
                cartas[i].selec=true;
            }
          }
        
        
    });
    
     canvas.addEventListener('mouseup', function(e){
        for (var i = 0; i < cartas.length; i++) {
            cartas[i].selec=false;
        }
    });
    
    canvas.addEventListener('mousemove', function(e){
        for (var i = 0; i < cartas.length; i++) {
            if (cartas[i].selec==true) {
                clear();
                cartas[i].x = e.clientX-100;
                cartas[i].y = e.clientY-100;
                cartas[i].draw();
            }
        }
    });
    
    
    ctx.drawImage(tablero,0,0,1000,800);
    for (var i = 0; i < cartas.length; i++) {
            cartas[i].draw();
    }
    
    
    window.requestAnimationFrame(draw);
}
